example.code:
private enum TestEnum
{
    [Abbreviation("ABBR1")]
    LongValue1,

    [Abbreviation("ABBR2")]
    LongValue2,
} 

public void Enum_ToAbbreviation()
{
    TestEnum value = TestEnum.Value1;

    string? abbreviation = value.ToAbbreviation();

    Assert.Equal("ABBR1", abbreviation);
}

